(1725, 0, 'In conducting an action under this rule, the court may issue orders that:', NULL, NULL, NULL, NULL),
(1726, 0, '(A) determine the course of proceedings or prescribe measures to prevent undue repetition or complication in presenting evidence or argument;', NULL, NULL, NULL, NULL),
