(1800, 0, '(B) may consider any other matter pertinent to counsel’s ability to fairly and adequately represent the interests of the class;', NULL, NULL, NULL, NULL),
(1801, 0, '(C) may order potential class counsel to provide information on any subject pertinent to the appointment and to propose terms for attorney’s fees and nontaxable costs;', NULL, NULL, NULL, NULL),
(1802, 0, '(D) may include in the appointing order provisions about the award of attorney’s fees or nontaxable costs under Rule 23', NULL, NULL, NULL, NULL),
