(2350, 0, '“‘A district court would necessarily abuse its discretion if it based its ruling on an erroneous view of the law or a clearly erroneous assessment of the evidence.’”', NULL, NULL, NULL, NULL),
(2351, 0, 'United States v. Hinkson, 585 F.3d 1247, 1259', NULL, NULL, NULL, NULL),
(2352, 0, '(9th Cir. 2009)', NULL, NULL, NULL, NULL),
(2353, 0, '(en banc)', NULL, NULL, NULL, NULL),
(2354, 0, '(quoting Cooter & Gell v. Hartmarx Corp., 496 U.S. 384, 405', NULL, NULL, NULL, NULL),
(2355, 0, '(1990)).', NULL, NULL, NULL, NULL),
(2356, 0, 'Thus, this Court has held, “when an appellant raises the argument that the district court premised a class certification determination on an error of law, our first task is to evaluate whether such legal error occurred.”', NULL, NULL, NULL, NULL),
(2357, 0, 'Yokoyama, 594 F.3d at 1091.', NULL, NULL, NULL, NULL),
